// src/components/Grid.js
import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Project_1 from "../assets/png/project_1.png"
import example_img from '../assets/jpeg/project-mockup-example.jpeg'
import { gridData } from "./GridData";
import "../Css/Grid.css"; // Import CSS file

const Grid = () => {
    return (
        <div>
            <section class="project-details">
                <div class="main-container">
                    <div class="project-details__content">

                       

                    </div>

                    <div className="grid-container">
                        {Array.from({ length: 12 }).map((_, index) => (
                            <div className="grid-item" key={index}>
                                <div class="projects__row-img-cont">
                                    <img
                                        src={example_img}
                                        alt="Software Screenshot"
                                        class="projects__row-img"
                                        loading="lazy"
                                    />
                                </div>

                                <div class="projects__row-content">
                                    <h3 class="projects__row-content-title">Project 1</h3>
                                    {/* <p class="projects__row-content-desc">
                                            Lorem ipsum dolor sit amet consectetur adipisicing elit. Hic
                                            facilis tempora, explicabo quae quod deserunt eius sapiente
                                            praesentium.
                                        </p> */}

                                    <Link
                                        to="/Project_info"
                                        class="btn btn--med btn--theme dynamicBgClr"
                                    // target="_blank"
                                    >Case Study
                                    </Link>
                                </div>


                                {/* {index + 1} */}
                            </div>
                        ))}
                    </div>



                    <div className="grid-container">
                        {gridData.map((item) => (
                            <div className="grid-item" key={item.id}>
                                <img src={item.imgUrl} alt={item.title} className="grid-image" />
                                <h3>{item.title}</h3>
                                <p>{item.description}</p>
                            </div>
                        ))}
                    </div>


                </div>
            </section>


        </div>
    );
};

export default Grid;
